import { useState } from "react";
import { View, TouchableOpacity, Text, TextInput, StyleSheet} from "react-native";

export default function App(){
  const [n1, setN1] = useState("");
  const [n2, setN2] = useState("")
  const [res, setRes] = useState("")


  return(
    <View>
      <TextInput placeholder="Primeiro numero" value={n1} onChangeText={(val) => { setN1(val);}}/>
      <TextInput placeholder="Segundo numero" value={n2} onChangeText={(val) => { setN2(val);}}></TextInput>
      <TouchableOpacity onPress={() => {setRes(parseInt(n1) + parseInt(n2))}}>
      <Text>+</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => { setRes(parseInt(n1) - parseInt(n2))}}>
      <Text>-</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => { setRes(parseInt(n1) / parseInt(n2))}}>
      <Text>/</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => { setRes(parseInt(n1) * parseInt(n2))}}>
      <Text>*</Text>
      </TouchableOpacity>
      <Text>{res}</Text>
    </View>
  );

}
