import { useState } from "react";
import { View, TouchableOpacity, Text, TextInput, StyleSheet} from "react-native";

export default function App(){
  const [n1, setN1] = useState("");
  const [n2, setN2] = useState("")
  const [res, setRes] = useState("")


  return(
    <View style={style.container}>
      <TextInput placeholder="Primeiro numero" value={n1} onChangeText={(val) => { setN1(val);}}/>
      <TextInput placeholder="Segundo numero" value={n2} onChangeText={(val) => { setN2(val);}}></TextInput>
      <View style={style.icons}>
      <TouchableOpacity onPress={() => {setRes(parseInt(n1) + parseInt(n2))}}>
      <Text>+</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => { setRes(parseInt(n1) - parseInt(n2))}}>
      <Text>-</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => { setRes(parseInt(n1) / parseInt(n2))}}>
      <Text>/</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => { setRes(parseInt(n1) * parseInt(n2))}}>
      <Text>*</Text>
      </TouchableOpacity>
      </View>
      <Text>{res}</Text>

    </View>
  );

}

const style = StyleSheet.create({
  container:{
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    display: 'flex',
    borderBottomColor: 'black',
    borderBottomWidth: '2px',
  },
  icons:{
    display: 'flex',

  }
})
